const express = require('express');
const bodyParser = require('body-parser');
const mongoose = require('mongoose');

const app = express();
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: true }));

// Replace with your MongoDB connection string
mongoose.connect('your_mongo_db_connection_string', { useNewUrlParser: true, useUnifiedTopology: true });

const donationSchema = new mongoose.Schema({
    donate: String,
    name: String,
    quantity: Number,
    address: String,
    sector: String,
    phone: String,
    collection: String,
});

const Donation = mongoose.model('Donation', donationSchema);

app.post('/submit-donation', async (req, res) => {
    try {
        const newDonation = new Donation(req.body);
        await newDonation.save();
        res.status(200).send('Donation successfully saved to the database!');
    } catch (err) {
        res.status(500).send('Failed to save donation.');
    }
});

const PORT = process.env.PORT || 3000;
app.listen(PORT, () => {
    console.log(`Server is running on port ${PORT}`);
});
